// Disease Identification Page JavaScript
document.addEventListener('DOMContentLoaded', function() {
    const diseaseGrid = document.getElementById('disease-grid');
    const loadingIndicator = document.getElementById('loading-indicator');
    const uploadBox = document.getElementById('upload-box');
    const plantImageInput = document.getElementById('plant-image');
    
    // Sample disease data
    const diseasesData = [
        {
            id: 1,
            name: "Late Blight (Potato & Tomato)",
            imageUrl: "https://www.greenlife.co.ke/wp-content/uploads/2022/04/Late-Blight-Tomatoes.jpg",
            description: "A serious disease affecting potatoes and tomatoes. It appears as water-soaked lesions that turn brown to purplish-black.",
            prevention: [
                "Use disease-resistant varieties",
                "Ensure good air circulation around plants",
                "Avoid overhead irrigation",
                "Apply fungicide preventatively"
            ]
        },
        {
            id: 2,
            name: "Powdery Mildew",
            imageUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSXlg02-c6PbCV2l2axEH14CSIZBuZxgqkuug&s",
            description: "A fungal disease that appears as white or gray powdery patches on leaves, stems, and sometimes fruits or flowers.",
            prevention: [
                "Space plants properly for good air circulation",
                "Avoid watering from overhead",
                "Remove and destroy infected plant parts",
                "Apply appropriate fungicides"
            ]
        },
        {
            id: 3,
            name: "Wheat Rust",
            imageUrl: "data:image/jpeg;base64,",
            description: "A fungal disease appearing as orange-red or brown pustules on wheat stems, leaves, and spikes.",
            prevention: [
                "Plant resistant varieties",
                "Early sowing to avoid peak disease periods",
                "Use balanced fertilization",
                "Apply fungicides at the first sign of disease"
            ]
        },
        {
            id: 4,
            name: "Rice Blast",
            imageUrl: "data:image/jpeg;base64,",
            description: "A devastating fungal disease of rice with diamond-shaped lesions that can affect all above-ground parts of the plant.",
            prevention: [
                "Plant resistant rice varieties",
                "Balanced nitrogen application",
                "Maintain appropriate water levels",
                "Use fungicides preventatively in high-risk areas"
            ]
        },
        {
            id: 5,
            name: "Cotton Leaf Spot",
            imageUrl: "data:image/jpeg;base64,",
            description: "A bacterial disease causing water-soaked spots on leaves that turn brown to black with yellow halos.",
            prevention: [
                "Use clean, disease-free seeds",
                "Practice crop rotation",
                "Avoid working in fields when plants are wet",
                "Apply copper-based bactericides preventatively"
            ]
        },
        {
            id: 6,
            name: "Corn Smut",
            imageUrl: "https://i.etsystatic.com/22398417/r/il/eb6a02/6252799350/il_fullxfull.6252799350_1hii.jpg",
            description: "A fungal disease causing galls on ears, tassels, and leaves of corn plants that rupture to release dark spores.",
            prevention: [
                "Plant resistant corn varieties",
                "Rotate crops regularly",
                "Remove and destroy infected plants",
                "Avoid injuring plants during cultivation"
            ]
        }
    ];
    
    // Handle file upload
    if (uploadBox && plantImageInput) {
        uploadBox.addEventListener('click', function() {
            plantImageInput.click();
        });
        
        plantImageInput.addEventListener('change', function() {
            if (this.files && this.files[0]) {
                const file = this.files[0];
                const reader = new FileReader();
                
                reader.onload = function(e) {
                    uploadBox.innerHTML = `
                        <img src="${e.target.result}" alt="Uploaded plant image" style="max-width: 100%; max-height: 200px; margin-bottom: 15px;">
                        <p>Image uploaded successfully</p>
                        <button class="btn btn-primary process-btn">
                            <i class="fas fa-search"></i> Identify Disease
                        </button>
                    `;
                    
                    // Add event listener to the identify button
                    const processBtn = uploadBox.querySelector('.process-btn');
                    if (processBtn) {
                        processBtn.addEventListener('click', function() {
                            simulateDiseaseIdentification();
                        });
                    }
                };
                
                reader.readAsDataURL(file);
            }
        });
    }
    
    // Simulate disease identification process
    function simulateDiseaseIdentification() {
        uploadBox.innerHTML = `
            <div class="processing">
                <div class="spinner"></div>
                <p>Analyzing image...</p>
                <p class="small-text">This may take a few moments</p>
            </div>
        `;
        
        // Simulate processing time
        setTimeout(() => {
            // Show a random disease as the result
            const randomDisease = diseasesData[Math.floor(Math.random() * diseasesData.length)];
            
            uploadBox.innerHTML = `
                <div class="result-box">
                    <h3>Identification Result</h3>
                    <div class="result-content">
                        <img src="${randomDisease.imageUrl}" alt="${randomDisease.name}" class="result-image">
                        <div class="result-details">
                            <h4>${randomDisease.name}</h4>
                            <p>${randomDisease.description}</p>
                            <a href="#" class="view-treatments">View detailed treatments</a>
                        </div>
                    </div>
                    <button class="btn btn-secondary mt-3 reset-btn">
                        <i class="fas fa-redo"></i> Upload Another Image
                    </button>
                </div>
            `;
            
            // Reset button functionality
            const resetBtn = uploadBox.querySelector('.reset-btn');
            if (resetBtn) {
                resetBtn.addEventListener('click', function() {
                    // Reset the upload box to original state
                    uploadBox.innerHTML = `
                        <i class="fas fa-camera"></i>
                        <p>Upload a photo of the affected plant</p>
                        <label for="plant-image" class="btn btn-primary">
                            <i class="fas fa-upload"></i> Upload Image
                        </label>
                    `;
                });
            }
        }, 2000);
    }
    
    // Load disease data
    if (diseaseGrid && loadingIndicator) {
        setTimeout(() => {
            loadingIndicator.style.display = 'none';
            renderDiseaseCards();
        }, 1000);
    }
    
    // Function to render disease cards
    function renderDiseaseCards() {
        if (!diseaseGrid) return;
        
        let cardsHTML = '';
        
        diseasesData.forEach(disease => {
            let preventionHTML = '';
            
            disease.prevention.forEach(tip => {
                preventionHTML += `
                    <li>
                        <i class="fas fa-check-circle"></i>
                        <span>${tip}</span>
                    </li>
                `;
            });
            
            cardsHTML += `
                <div class="disease-card">
                    <div class="disease-image">
                        <img src="${disease.imageUrl}" alt="${disease.name}">
                    </div>
                    <div class="disease-details">
                        <h3 class="disease-name">${disease.name}</h3>
                        <p class="disease-description">${disease.description}</p>
                        <h4 class="prevention-title">Prevention & Treatment:</h4>
                        <ul class="prevention-list">
                            ${preventionHTML}
                        </ul>
                        <a href="#" class="disease-link">Read detailed guide <i class="fas fa-arrow-right"></i></a>
                    </div>
                </div>
            `;
        });
        
        diseaseGrid.innerHTML = cardsHTML;
    }
});